const inter = Inter({ subsets: ["latin"] });

const globalSans = localFont({
  src: '../public/GeneralSans_Complete/Fonts/TTF/GeneralSans-Variable.ttf',
  // weight: `['100','200','400','500', '600', '700', '800']`,
  variable: '--font-gsans',
})

