export default function RootLayout({ children }) {
  const pathname = usePathname();
  const requiredPath = pathname?.split("/")[1];
  
  return (
    <html lang="en">
      <body className={`${globalSans.variable} font-gsans max-h-screen overflow-y-scroll`}>
        <NavBar/>
        { requiredPath && requiredPath === 'jobs' &&  <NavigationBar/>}
          { children }
        </body>
    </html>
  );
}
